import { Component } from '@angular/core';
import { Attendance } from 'src/app/class/attendance';

@Component({
  selector: 'app-log',
  templateUrl: './log.component.html',
  styleUrls: ['./log.component.css']
})
export class LogComponent {

  public att:Attendance  = new Attendance();
  public currentTime: any='';

  constructor(private route:Router,private serv:DataService){}

  ngOnInit(){
    let x =sessionStorage.getItem('username');
    if(x!=null && !sessionStorage.getItem('y') ){
      document.location.reload();
     sessionStorage.setItem('y',"1");
    }

    this.gettodaydata();

    this.updateTime();
    setInterval(() => this.updateTime(), 1000); // Update the time every second

  }
  updateTime(): void {
    const now = new Date();
    this.currentTime = now.toLocaleTimeString('en-US', { hour: 'numeric', minute: 'numeric', second: 'numeric', hour12: true });
  }
  gettodaydata(){
    let id = sessionStorage.getItem('id');
    console.log(id);
      this.serv.gettodaydata(id).subscribe(data=>{
        this.att = data;
        console.log("Data found" + data)
      })

    }


  signIn(){
    this.att.signIn = this.currentTime;
    console.log(this.att);
    this.serv.updatesignintime(this.att).subscribe(data=>{
      console.log("signin time updated");
    })

  }
  signOut(){
    this.att.signOut = this.currentTime;
    console.log(this.att);
    this.serv.updatesignOuttime(this.att).subscribe(data=>{
      console.log("signin time updated");
    })

  }

  gotomyreport(){
      let id =sessionStorage.getItem('id')
    this.route.navigate(['myreport',id]);
  }
}
