import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Attendance } from 'src/app/class/attendance';
import { DataService } from 'src/app/services/data.service';

@Component({
  selector: 'app-report',
  templateUrl: './report.component.html',
  styleUrls: ['./report.component.css']
})
export class ReportComponent {
  public att:Attendance[] = [];
  public id:any = '';
  public date = new Date();

  constructor(private route:ActivatedRoute, private serv:DataService){}
  ngOnInit(){
      this.route.paramMap.subscribe(data=>{
          this.id = data.get('id')
          console.log(this.id);
      })

      this.serv.getmydata(this.id).subscribe(data2=>{
        this.att=data2;
        console.log("Data fetched");
        console.log(data2);
      })
  }

}

<div   >
    <div class="card text-center"  style="max-width: 30%;margin-left: 35%;margin-top: 5%;">

      <div  class="card-body">

      <table class="table">
          <tbody>
            <tr *ngFor="let i of att | async as att1;">
                <th scope="row" > 
                    <div *ngIf="i.date != att1[i-1].date"> {{i.date}}</div>
                    <div>Sign in : <span *ngIf="date!=i.date && !i.signIn">Absent</span> {{i.signOut}} </div>
                    <div>Sign Out: <span *ngIf="date!=i.date && !i.signIn">Absent</span> {{i.signOut}}</div>
                </th>
            </tr>
          </tbody>
        </table>
      </div>
    </div>

  </div>
